import React, { FC, useState } from 'react';
import { IUser } from "../../types/response";
import { ESort } from "../../enums";

import {
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Button,
} from '@mui/material';

interface IProps {
  data: IUser[];
};

export const UserTable: FC<IProps> = ({ data }) => {
    const [sortedData, setSortedData] = useState<IUser[]>(data);
    const [sortDirection, setSortDirection] =
        useState<ESort>(ESort.ASC);

    const handleSort = (): void => {
        const newSortDirection = sortDirection === ESort.ASC ? ESort.DESC : ESort.ASC;
        setSortDirection(newSortDirection);

        const sorted = [...data].sort((a, b) => {
            const fieldA = a.username.toLowerCase();
            const fieldB = b.username.toLowerCase();

            if (newSortDirection === ESort.ASC) {
                return fieldA.localeCompare(fieldB);
            } else {
                return fieldB.localeCompare(fieldA);
            }
        });

        setSortedData(sorted);
    };

    return (
        <TableContainer component={Paper}>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>Name</TableCell>
                        <TableCell>
                            Username
                            <Button onClick={handleSort}>
                                {sortDirection === 'asc' ? '▼' : '▲'}
                            </Button>
                        </TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {data.map(({ id, name, username }) => (
                        <TableRow key={id}>
                            <TableCell>{id}</TableCell>
                            <TableCell>{name}</TableCell>
                            <TableCell>{username}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};
